using System.Collections;using UnityEngine;namespace SchildeTools.GameEvents{    public class DelayedVoidListener : BaseGameEventListener<Void, VoidEvent, UnityVoidEvent>    {        [SerializeField] private float delay;        [SerializeField] private UnityVoidEvent delayedUnityEventResponse;        private void Awake() => DontDestroyOnLoad(this);        public override void OnEventRaised(Void item)        {            unityEventResponse?.Invoke(item);            StartCoroutine(RaiseDelayedEvent(item));        }        private IEnumerator RaiseDelayedEvent(Void item)        {            yield return new WaitForSeconds(delay);            delayedUnityEventResponse?.Invoke(item);        }    }}